import React from 'react';

const Weather = (props) => {
  let capitalizedDesc = props.description.charAt(0).toUpperCase() + props.description.slice(1);
  let sunrise = props.sunrise.date.toLocaleTimeString('en-US');
  let sunset = props.sunset.date.toLocaleTimeString('en-US');

  return(
    <div>
      <h1>{props.city}</h1>
      <h2>{props.temperature}°F - {capitalizedDesc}</h2>
      <p>High: {props.high}°F | Low: {props.low}°F</p>
      <p>Humidity: {props.humidity} %</p>
      <p>Wind Speed: {props.wind} mps</p>
      <p>Sunrise: {sunrise} | Sunset: {sunset}</p>
    </div>
  )
}

export default Weather;
